frappe.web_form.after_load = () => {
    // Get the flight name from the form
    const flight_name = frappe.web_form.get_value('flight');  // Get the selected flight value

    console.log("Flight Name:", flight_name);  // Debugging log to check the flight name

    // Check if the flight name is not empty
    if (flight_name && flight_name.trim() !== '') {
        // Fetch the flight price based on the flight name
        frappe.call({
            method: 'frappe.client.get',
            args: {
                doctype: 'Airplane Ticket',
                filters: {
                    flight: flight_name  // Use the selected flight name as a filter
                }
            },
            callback: function(response) {
                console.log("Response from API:", response);  // Debugging log to check the response

                if (response.message) {
                    // Assuming the price field is 'flight_price'
                    frappe.web_form.set_value('flight_price', response.message.flight_price);  // Set the flight price
                    console.log("Flight Price Set:", response.message.flight_price);  // Debugging log to confirm price set
                } else {
                    frappe.msgprint(__('No matching flight found'));  // If no result, show message
                    console.log("No matching flight found");  // Debugging log for missing flight
                }
            },
            error: function(error) {
                console.log("Error:", error);  // Log any errors from the API call
            }
        });
    } else {
        // If flight name is empty or not selected
        console.log("No flight selected or field is empty.");  // Debugging log if no flight is selected
    }
};
